package level5;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(int i=1;i<=3;i++)
       {
    	   
    		   if(i==2)
    		   {
    			 for(int k=1;k<=2;k++) {
    				   System.out.print("*");
    			 
    				   for(int p=1;p<=3;p++)
    				   {
    					   System.out.print(" ");
    				   }
    			   }
    			   
    		    
    		   }
    		   else {
    		   for(int h=1;h<=3;h++)
    		   System.out.print("* ");
    		   }
    		   System.out.println();
    	   }
    	   
       }
	}


